(837, 241, '_banner_headline', 'field_5c4a47b14bbde'),
(838, 241, 'banner_text', 'Our Mission is to live up to our company name on every project from start to finish.'),
(839, 241, '_banner_text', 'field_5c4a47b94bbdf'),
(840, 241, 'banner_image', '237'),
