(2426, 262, 'banner_headline', 'WHY WORLD CLASS CONTRACTING'),
(2427, 262, '_banner_headline', 'field_5c4a47b14bbde'),
(2428, 262, 'banner_text', 'Our Mission is to live up to our company name on every project from start to finish.'),
(2429, 262, '_banner_text', 'field_5c4a47b94bbdf'),
(2430, 262, 'banner_image', '261'),
(2431, 262, '_banner_image', 'field_5c4a47c14bbe0'),
