(1355, 158, '_section_3_content_0_headline', 'field_5c4a484e4bbe7'),
(1356, 158, 'section_3_content_0_text', 'The corporation was founded in 1999 out of Dickson Alberta, and then moved on to establish operation headquarters out of Red Deer Alberta in 2005. The company has continued to grow to become one of the most prominent Mechanical Contractors in Central Alberta.'),
(1357, 158, '_section_3_content_0_text', 'field_5c4a48554bbe8'),
(1358, 158, 'section_3_content_0_image', '104'),
(1359, 158, '_section_3_content_0_image', 'field_5c4a481d4bbe6'),
